import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:pull_to_refresh/pull_to_refresh.dart';

import 'yc_header_animation.dart';

const String IMAGE_NO_MORE =
    "";

const String IMAGE_REFRESH_FIRST_FRAME = "";
Widget firstFrame = FirstFrame();

class YCRefresh extends StatelessWidget {
  final Widget child;
  final bool enableRefresh;

  final bool enableLoadMore;

  final VoidCallback onRefresh;
  final VoidCallback onLoading;
  final RefreshController controller;

  const YCRefresh(
      {Key key,
      this.child,
      this.enableLoadMore = true,
      this.enableRefresh = true,
      this.onRefresh,
      this.onLoading,
      this.controller})
      : super(key: key);



  @override
  Widget build(BuildContext context) {

    return RefreshConfiguration(
        headerTriggerDistance: 91, // 控件bug，触发距离必须大于header高度
        autoLoad: true,
        child: SmartRefresher(
          controller: controller,
          enablePullDown: enableRefresh,
          enablePullUp: enableLoadMore,
          header: _header(),
          footer: _footer(),
          child: child,
          onRefresh: onRefresh,
          onLoading: onLoading,
        ));
  }

  _header() {
    return CustomHeader(
      height: 90,
      completeDuration: Duration.zero,
      builder: (BuildContext context, RefreshStatus mode) {
        Widget tipText;
        if (mode == RefreshStatus.idle) {
          tipText = Text(
            "下拉刷新",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else if (mode == RefreshStatus.refreshing) {
          tipText = Text(
            "正在刷新",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else if (mode == RefreshStatus.failed) {
          tipText = Text(
            "刷新失败",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else if (mode == RefreshStatus.canRefresh) {
          tipText = Text(
            "松开即可刷新",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else {
          tipText = Text(
            "下拉刷新",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        }

        Widget refresh;

        if(mode == RefreshStatus.refreshing){
          refresh = LoadingAnimation();
        }else{
          refresh = firstFrame;
        }

        return Container(
          height: 90,
          child: Column(
            children: <Widget>[refresh, tipText],
          ),
        );
      },
    );
  }

  _footer() {
    return CustomFooter(
      builder: (BuildContext context, LoadStatus mode) {
        Widget body;
        if (mode == LoadStatus.idle) {
          body = Text(
            "上拉加载",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else if (mode == LoadStatus.loading) {
          body = Row(
            mainAxisAlignment: MainAxisAlignment.center,
            children: <Widget>[
              SizedBox(
                width: 20,
                height: 20,
                child: CircularProgressIndicator(
                  strokeWidth: 2.0,
                  valueColor: AlwaysStoppedAnimation(Colors.grey[400]),
                ),
              ),
              Padding(
                child: Text(
                  "正在加载",
                  style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
                ),
                padding: EdgeInsets.only(left: 6),
              )
            ],
          );
        } else if (mode == LoadStatus.failed) {
          body = Text(
            "加载失败",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else if (mode == LoadStatus.canLoading) {
          body = Text(
            "释放立即加载",
            style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
          );
        } else {
          body = Row(
            mainAxisAlignment: MainAxisAlignment.center,
            children: <Widget>[
              Image.memory(
                base64Decode(IMAGE_NO_MORE),
                scale: 3,
              ),
              Padding(
                child: Text(
                  "没有更多啦！",
                  style: TextStyle(color: Color.fromARGB(255, 145, 145, 145), fontSize: 12),
                ),
                padding: EdgeInsets.only(left: 6),
              )
            ],
          );
        }
        return Container(
          height: 80.0,
          child: Center(child: body),
        );
      },
    );
  }
}

class FirstFrame extends StatefulWidget {
  @override
  _FirstFrameState createState() => _FirstFrameState();
}

class _FirstFrameState extends State<FirstFrame> with AutomaticKeepAliveClientMixin{
  @override
  Widget build(BuildContext context) {
    super.build(context);
    return Container(
      width: 60,
      height: 60,
      child: Image.memory(
        base64Decode(IMAGE_REFRESH_FIRST_FRAME),
        scale: 3,
      ),
    );
  }

  @override
  bool get wantKeepAlive => true;
}

